import { Grid, Formatters, Editors} from 'slickgrid-es6';
import data from '../stubs/sample_data';
import {requiredFieldValidator} from '../utilities/utils'

const flatPickrOptions = {
  dateFormat: 'd/m/Y', 
  parseDate: function(input){
    var split = input.split('/');
    return new Date(split[1] + '-' + split[0] + '-' + split[2]);
  }
};

const columns = [
  {id: 'travelId', name: 'Travel Id', field: 'travelId', minWidth: 90, editor: Editors.Date, options: { date: flatPickrOptions }},
  {id: 'finish', name: 'Flight No', field: 'flight', minWidth: 90},
  {id: 'title', name: 'Date', field: 'flight', cssClass: 'cell-title', editor: Editors.Text, maxWidth: 100, minWidth: 80, validator: requiredFieldValidator},
  // {id: 'duration', name: 'Segment From', field: 'segment', resizable: false},
  // {id: 'start', name: 'Segment To', field: 'segment', minWidth: 90, editor: Editors.Date,options: { date: flatPickrOptions }},
  // {id: 'finish', name: 'flightModel', field: 'details', minWidth: 90},
  // {id: 'effort-driven', name: 'bodyType', field: 'details'},
  // {id: 'title', name: 'type', field: 'details', maxWidth: 100, minWidth: 80},
  // {id: 'duration', name: 'startTime', field: 'details', resizable: false},
  // {id: 'start', name: 'endTime', field: 'details', minWidth: 90, editor: Editors.Date,options: { date: flatPickrOptions }},
  // {id: 'finish', name: 'status', field: 'details'},
  // {id: 'effort-driven', name: 'additionalStatus', field: 'details'},
  // {id: 'title', name: 'timeStatus', field: 'details', maxWidth: 100, minWidth: 80},
  // {id: 'duration', name: 'percentage', field: 'weight', resizable: false},
  // {id: 'finish', name: 'value', field: 'weight'},
  // {id: 'start', name: 'percentage', field: 'volume', minWidth: 90, editor: Editors.Date,options: { date: flatPickrOptions }},
  // {id: 'finish', name: 'value', field: 'volume'},
  // {id: 'effort-driven', name: 'position', field: 'uldPositions'},
  // {id: '%', name: 'value', field: 'uldPositions', formatter: Formatters.PercentCompleteBar}
];
const options = {
  enableCellNavigation: true,
  enableColumnReorder: true,
  forceFitColumns: !true,
  frozenColumn: 0,
  frozenRow: 1,
  editable: true,
  enableAddRow: true,
  asyncEditorLoading: false,
  autoEdit: false
};
export default () => new Grid("#iCargoSpreadSheet", data, columns, options);
