const columns = [
  {id: 'travelId', name: 'Travel Id', field: 'travelId', minWidth: 90, editor: Editors.Date},
  {id: 'flight', name: 'Flight No', field: 'flight', minWidth: 90, editor: Editors.Text,formatter: iCargoFormatter},
  {id: 'flight', name: 'Flight Date', field: 'flight', cssClass: 'cell-title', editor: Editors.Text,options: { date: flatPickrOptions }, maxWidth: 100, minWidth: 80, validator: requiredFieldValidator, },
  // {id: 'duration', name: 'Segment From', field: 'segment', resizable: false},
  // {id: 'start', name: 'Segment To', field: 'segment', minWidth: 90, editor: Editors.Date,options: { date: flatPickrOptions }},
